import { useState } from "react";
import { categories } from "../data/category";
import { product } from "../data/product";
import classes from "./Shop.module.css";
function Shop(){
    const[selectedCategory,setCategory] = useState(null);
    const onClickCategoryHandler=(cat_id) =>{
        setCategory(cat_id);
    };
    console.log(selectedCategory);
    let filterProduct = [...pro];
    if(selectedCategory != null){
        filterProduct = pro.filter(product => product.categoryId == selectedCategory);
    }
    return <div className={classes.container}>
        <div className={classes.row}>
            <h1>Shop của Hoài Huy</h1>
            
        </div>
        <div className={classes.row}>
            <div className={classes.left}><h2>Danh mục sản phẩm</h2>
            {
                categories.map((cate) => (<div className={classes.cat} key={cate.id} onClick={()=> onClickCategoryHandler(cate.id)}>
                    {cate.name}
                </div>)
            )}
            </div>
            <div className={classes.right}>
                <h2>Sản phẩm</h2>
            <div className={classes.boxes}>
            {
                product.map((pro) => (<div className={classes.pro} key={pro.id}>
                   <h3>{pro.name}</h3> 
                   <img src={pro.imageUrl} className={classes.proding}/>
                   <h4>{pro.price} đ</h4>
                </div>)
            )}
            </div>
            </div>
        </div>
    </div>
}
export default Shop;
