import React from 'react';
import { useNavigate } from 'react-router-dom';
import classes from './DetailHelp.module.css';
import anh from '../406995021_882709106637797_4689841829377960494_n.jpg';

function DetailHelp() {
  const navigate = useNavigate();

  const onLogoClickHandler = () => {
    navigate('/shop');
  };

  return (
    <div className={classes.container}>
      <header className={classes.header}>
        <img src={anh} alt="Logo" className={classes.logo} onClick={onLogoClickHandler} />
        <span className={classes.headerText}>Trung tâm trợ giúp T1 Kon Shop</span>
        <span className={classes.headerPolicies}>T1 Shop Policies</span>
      </header>
      <div className={classes.searchBar}>
        <input
          type="text"
          placeholder="Nhập từ khóa hoặc nội dung cần tìm"
          aria-label="Search"
          className={classes.searchInput}
        />
        <button className={classes.searchButton}>
          <i className="fa fa-search" aria-hidden="true"></i>
        </button>
      </div>
      <div className={classes.mainContent}>
        <div className={classes.menuContainer}>
          <p className={classes.paragraph}>Mua Sắm Cùng Shopee: Tận hưởng trải nghiệm mua sắm thú vị với Shopee. Chúng tôi cung cấp đa dạng sản phẩm từ thời trang, điện tử, gia dụng đến các mặt hàng tiêu dùng hàng ngày.</p>
          <p className={classes.paragraph}>Khuyến Mãi & Ưu Đãi: Đừng bỏ lỡ những chương trình khuyến mãi hấp dẫn và ưu đãi đặc biệt dành cho bạn. Hãy theo dõi Shopee để cập nhật thông tin khuyến mãi mới nhất.</p>
          <p className={classes.paragraph}>Thanh Toán: Chúng tôi hỗ trợ nhiều phương thức thanh toán an toàn và tiện lợi, bao gồm thanh toán khi nhận hàng, thẻ tín dụng, và ví điện tử.</p>
          <p className={classes.paragraph}>Đơn Hàng & Vận Chuyển: Theo dõi đơn hàng của bạn và tìm hiểu thêm về các dịch vụ vận chuyển mà Shopee cung cấp. Chúng tôi cam kết giao hàng nhanh chóng và an toàn.</p>
          <p className={classes.paragraph}>Trả Hàng & Hoàn Tiền: Chính sách trả hàng và hoàn tiền minh bạch giúp bạn yên tâm mua sắm. Xem hướng dẫn chi tiết về quy trình trả hàng và yêu cầu hoàn tiền tại Shopee.</p>
          <p className={classes.paragraph}>Thông Tin Chung: Tìm hiểu thêm về Shopee và các thông tin cần thiết khác. Chúng tôi luôn sẵn sàng hỗ trợ bạn 24/7.</p>
        </div>
      </div>
    </div>
  );
}

export default DetailHelp;
