import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import classes from './DetailHelp.module.css';
import anh from '../406995021_882709106637797_4689841829377960494_n.jpg';
import MenuItem from './MenuItem';

function DetailHelp() {
  const [activeItem, setActiveItem] = useState(null);
  const navigate = useNavigate();

  const onLogoClickHandler = () => {
    navigate('/shop');
  };

  const handleMenuItemClick = (item) => {
    setActiveItem(item === activeItem ? null : item);
  };

  return (
    <div className={classes.container}>
      <header className={classes.header}>
        <img src={anh} alt="Logo" className={classes.logo} onClick={onLogoClickHandler} />
        <span className={classes.headerText}>Trung tâm trợ giúp T1 Kon Shop</span>
        <span className={classes.headerPolicies}>T1 Shop Policies</span>
      </header>
      <div className={classes.searchBar}>
        <input
          type="text"
          placeholder="Nhập từ khóa hoặc nội dung cần tìm"
          aria-label="Search"
          className={classes.searchInput}
        />
        <button className={classes.searchButton}>
          <i className="fa fa-search" aria-hidden="true"></i>
        </button>
      </div>
      <div className={classes.mainContent}>
        <div className={classes.menuContainer}>
          <MenuItem
            title="Mua Sắm Cùng Shopee"
            onClick={() => handleMenuItemClick('shopee')}
            isActive={activeItem === 'shopee'}
          >
            Người dùng mới: Tận hưởng các ưu đãi và khuyến mãi đặc biệt dành cho người dùng mới trên Shopee. Khám phá những sản phẩm hot và giá cả phải chăng chỉ có trên Shopee.
          </MenuItem>
          <MenuItem
            title="Khuyến Mãi & Ưu Đãi"
            onClick={() => handleMenuItemClick('khuyenMai')}
            isActive={activeItem === 'khuyenMai'}
          >
            {/* Add content here */}
          </MenuItem>
          <MenuItem
            title="Thanh Toán"
            onClick={() => handleMenuItemClick('thanhToan')}
            isActive={activeItem === 'thanhToan'}
          >
            {/* Add content here */}
          </MenuItem>
          <MenuItem
            title="Đơn Hàng & Vận Chuyển"
            onClick={() => handleMenuItemClick('donHang')}
            isActive={activeItem === 'donHang'}
          >
            {/* Add content here */}
          </MenuItem>
          <MenuItem
            title="Trả Hàng & Hoàn Tiền"
            onClick={() => handleMenuItemClick('traHang')}
            isActive={activeItem === 'traHang'}
          >
            {/* Add content here */}
          </MenuItem>
          <MenuItem
            title="Thông Tin Chung"
            onClick={() => handleMenuItemClick('thongTin')}
            isActive={activeItem === 'thongTin'}
          >
            {/* Add content here */}
          </MenuItem>
        </div>
      </div>
    </div>
  );
}

export default DetailHelp;
