import React, { useState, useEffect } from 'react';
import classes from './ImageSlider.module.css';

const ImageSlider = ({ images }) => {
  const [current, setCurrent] = useState(0);
  const length = images.length;

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent(current => (current === length - 1 ? 0 : current + 1));
    }, 3000); // Chuyển đổi mỗi 3 giây

    return () => clearInterval(interval);
  }, [length]);

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  if (!Array.isArray(images) || images.length <= 0) {
    return null;
  }

  return (
    <section className={classes.slider}>
      <button className={classes.leftArrow} onClick={prevSlide}>&#10094;</button>
      {images.map((img, index) => (
        <div
          className={index === current ? `${classes.slide} ${classes.active}` : classes.slide}
          key={index}
        >
          {index === current && <img src={img} alt="product image" className={classes.image} />}
        </div>
      ))}
      <button className={classes.rightArrow} onClick={nextSlide}>&#10095;</button>
    </section>
  );
};

export default ImageSlider;
