import { useEffect, useState } from "react";
import { product } from "../data/product";
import classes from "./Giohang.module.css";
function Giohang({setShowCart,cart, setCart}){
    const [tongtien,setTongtien] = useState(0);
    const thaydoisoluong = (sanpham,sl) => {
    const idx = cart.indexOf(sanpham);
    const arr = [...cart];
    arr[idx].amount += sl;
    if(arr[idx].amount == 0) arr[idx].amount = 1;
    setCart([...arr]);
    }
    const removeProduct = (sanpham) =>{
        const arr = cart.filter(sp => sp.id !== sanpham.id);
        setCart({...arr});
    }
    const tinhtongtien = () => {
        let tt=0; 
        cart.map(sp => {
            tt += sp.price * sp.amount;
        }

        );
        setTongtien(tt);
    }
    useEffect(() => {
        tinhtongtien();
})
    const onCloseCartHandler = () => {
        setShowCart(false)
    };
    return <><h1>Giỏ hàng</h1>
    {
        cart.map(product =>(
            <div className={classes.row}>
                <div className={classes.img}>
                    <img src={product.imageUrl}/>
                    <div className={classes.title}>
                        {product.name}
                    </div>
                    <div className={classes.controls}>
                        <button onClick={() => thaydoisoluong (product,1)}>+</button>
                        <input type= "Text" value={product.amount} readOnly={true}/>
                        <button onClick={() => thaydoisoluong (product,-1)}>-</button>
                    </div>
                    <div className={classes.thanhtien}>
                        {product.price * product.amount} đ  
                    </div>
                    <button onClick={()=> removeProduct(product)}>Remove</button>
                </div>
            </div>

        ))
    }
    <h2>Tổng tiền thanh toán: {tongtien}</h2>
<button onClick={onCloseCartHandler}>Về trang chủ</button>
    </>
}
export default Giohang;
