import { useEffect, useState } from "react";
import { categories } from "../data/category";
import { product } from "../data/product";
import classes from "./Shop.module.css";
import Modal from "./Modal";
import Header from "./Header";

function Shop() {
  const [selectedCategory, setCategory] = useState(null);
  const [selectedProduct, setProduct] = useState(null);
  const [isShowModal, setShowModal] = useState(false);
  const [cart,setCart] = useState([]);
  const onClickCategoryHandler = (cat_id) => {
    setCategory(cat_id);
  };

  const onClickProductHandler = (product) => {
    setProduct(product);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };
  const onClickAddtoCartHandler = (product) =>{
    if (cart.indexOf(product) !== -1 ) return null;
    const arr = [...cart];
    product.amount = 1;
    arr.push(product);
    setCart([...arr]);
   }
useEffect(()=>{console.log(cart);})

  let filterProduct = [...product];
  if (selectedCategory != null) {
    filterProduct = product.filter((product) => product.categoryId === selectedCategory);
  }

  return (
    <div className={classes.container}>
      <Header />
      <div className={classes.row}>
        <h1>Shop của Hoài Huy</h1>
        {isShowModal && (
          <Modal closeModal={closeModal}>
            <div className={classes.row}>
              <div className={classes.left}>
                <img src={selectedProduct.imageUrl} className={classes.proding} alt={selectedProduct.name} />
              </div>
              <div className={classes.right}>
                <h3>{selectedProduct.name}</h3>
                <h4>{selectedProduct.price}</h4>
                <p>{selectedProduct.description}</p>
              </div>
            </div>
          </Modal>
        )}
      </div>
      <div className={classes.row}>
        <div className={classes.left}>
          <h2>Danh mục sản phẩm</h2>
          {categories.map((cate) => (
            <div className={classes.cat} key={cate.id} onClick={() => onClickCategoryHandler(cate.id)}>
              {cate.name}
            </div>
          ))}
        </div>
        <div className={classes.right}>
          <h2>Sản phẩm</h2>
          <div className={classes.boxes}>
            {filterProduct.map((pro) => (
              <div className={classes.pro} key={pro.id} >
                <h3>{pro.name}</h3>
                <img src={pro.imageUrl} className={classes.proding} alt={pro.name} />
                <h4>{pro.price} đ</h4>
                <button onClick={() => onClickProductHandler(pro)} >Xem chi tiết</button> &nbsp;
                {
                  cart.indexOf(product) !== -1 ?(
                    <span className={classes.datontai}>
                      Sản phẩm đã được thêm vào giỏ hàng
                    </span>
                  ):(<button onClick={() => onClickAddtoCartHandler(pro)}>Đặt hàng</button>)
                } 
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Shop;
