import React from 'react';
import PropTypes from 'prop-types';
import classes from './MenuItem.module.css';

function MenuItem({ title, isOpen, children, onClick, onSubItemClick, selected }) {
  const menuItemClass = selected ? `${classes.menuItem} ${classes.menuItemSelected}` : classes.menuItem;

  return (
    <div className={menuItemClass}>
      <div className={classes.menuTitle} onClick={onClick}>
        {title}
      </div>
      {isOpen && (
        <div className={classes.menuContent}>
          {React.Children.map(children, (child) =>
            React.cloneElement(child, {
              onClick: () => onSubItemClick(child.props.children)
            })
          )}
        </div>
      )}
    </div>
  );
}

MenuItem.propTypes = {
  title: PropTypes.string.isRequired,
  isOpen: PropTypes.bool.isRequired,
  children: PropTypes.node.isRequired,
  onClick: PropTypes.func.isRequired,
  onSubItemClick: PropTypes.func.isRequired,
  selected: PropTypes.bool
};

export default MenuItem;
